import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../css/signin.css';
import { Helmet } from 'react-helmet';

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [reenterPassword, setReenterPassword] = useState(''); // Declare reenterPassword
  const [email, setEmail] = useState(''); // Declare email
  const [role, setRole] = useState('Buyer'); // Declare role
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    // Basic validation
  if (username.trim() === '' || password.trim() === '' || reenterPassword.trim() === '' || email.trim() === '') {
    setError('Please fill in all fields.');
    return;
  }

  if (password !== reenterPassword) {
    setError('Passwords do not match. Please reenter.');
    return;
  }

  try {
    const userData = {
      username,
      password,
      email,  // Define the 'email' variable here
      role,   // Define the 'role' variable here
    };

    const response = await fetch('/api/users/registration', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    });

    if (response.ok) {
      // Assume a successful signup after a short delay
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // Redirect to the signin page after successful signup
      navigate('/');
    } else {
      const errorMessage = await response.json();
      setError(`Error during signup: ${errorMessage.error}`);
    }
  } catch (error) {
    setError('Error during signup. Please try again.');
  }

    // if (username.trim() === '' && password.trim() === '') {
    //   setError('Please enter both username and password.');
    //   return;
    // }

    // if (username.trim() === '') {
    //   setError('Please enter your username.');
    //   return;
    // }

    // if (password.trim() === '') {
    //   setError('Please enter your password.');
    //   return;
    // }

    // if (username === 'user123' && password === 'Demo@123') {
    //   console.log('Signing in...');
    //   setError('');
    //   navigate('/homepage'); 
    // } else {
    //   setError('Incorrect username or password. Please check your credentials and try again.');
    // }
    
  };

  return (
    <div className="app-container">
            <Helmet>
        <meta charset="utf-8" />
        <link rel="icon" href="img/favicon.png" />
        <link rel="apple-touch-icon" href="img/favicon.png" />
        <title>Auction Platform - SignIn</title>
      </Helmet>
      <h1 className="title">Auction Platform</h1>

      <div className="sign-in-container">
        <h2 className="sign-in-heading">Sign In</h2>
        <form className="sign-in-form" onSubmit={handleSignIn}>
          <label htmlFor="username"><strong>Username:</strong></label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <label htmlFor="password"><strong>Password:</strong></label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <p className="error-message">{error}</p>}

          <div className="form-links">
            <Link to="/forgetpassword">Forgot Password?</Link>
            <span className="separator">|</span>
            <Link to="/signup">Sign Up</Link>
          </div>
          <button type="submit" className="sign-in-button">
            Sign In
          </button>
        </form>
      </div>

      <div className="footer">
        <p>&copy; 2024 Done by <strong>Kaliraj M & Sridhar R</strong>. All rights reserved.</p>
      </div>
    </div>
  );
};

export default SignInPage;


