import React, { useState } from 'react';
import { Link, useNavigate, Navigate   } from 'react-router-dom';
import '../css/signin.css';
import { Helmet } from 'react-helmet';
import axios from 'axios'; // Import axios

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  const handleSignIn = (e) => {
    e.preventDefault();

    if (username.trim() === '' && password.trim() === '') {
      setError('Please enter both username and password.');
      return;
    }

    if (username.trim() === '') {
      setError('Please enter your username.');
      return;
    }

    if (password.trim() === '') {
      setError('Please enter your password.');
      return;
    }

    axios.post('http://localhost:8080/api/users/login', {
      username: username,
      password: password,
      role: userRole,
    })
    .then(response => {
      console.log(response.data);
      setError('');
      if (userRole === 'Buyer') {
        return <Navigate to="/homepage" />;
      } else if (userRole === 'Seller') {
        return <Navigate to="/seller" />;
      } else if (userRole === 'Admin') {
        return <Navigate to="/homepage" />;
      } else {
        // Handle other roles or unexpected scenarios
        return <div>Unknown user role</div>;
    }
    })
    .catch(error => {
      setError('Incorrect username or password. Please check your credentials and try again.');
    });
  };

  return (
    <div className="app-container">
      <Helmet>
        <meta charset="utf-8" />
        <link rel="icon" href="img/favicon.png" />
        <link rel="apple-touch-icon" href="img/favicon.png" />
        <title>Auction Platform - SignIn</title>
      </Helmet>
      <h1 className="title">Auction Platform</h1>

      <div className="sign-in-container">
        <h2 className="sign-in-heading">Sign In</h2>
        <form className="sign-in-form" onSubmit={handleSignIn}>
           <label htmlFor="username"><strong>Username:</strong></label>
           <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

           <label htmlFor="password"><strong>Password:</strong></label>
           <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

           {error && <p className="error-message">{error}</p>}

           <div className="form-links">
             <Link to="/forgetpassword">Forgot Password?</Link>
             <span className="separator">|</span>
             <Link to="/signup">Sign Up</Link>
           </div>
          <button type="submit" className="sign-in-button">
            Sign In
          </button>
        </form>
      </div>

      <div className="footer">
        <p>&copy; 2024 Done by <strong>Kaliraj M & Sridhar R</strong>. All rights reserved.</p>
      </div>
    </div>
  );
};

export default SignInPage;


