import React, { useState, useEffect, useRef } from 'react';
import { VscAccount } from 'react-icons/vsc';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom

const HomePage = () => {
  const [isDropdownVisible, setDropdownVisibility] = useState(false);
  const dropdownRef = useRef(null);
  const history = useNavigate(); // Initialize useHistory

  const toggleDropdown = () => {
    setDropdownVisibility(!isDropdownVisible);
  };

  const closeDropdown = () => {
    setDropdownVisibility(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        closeDropdown();
      }
    };

    document.addEventListener('click', handleClickOutside);

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isDropdownVisible]);

  return (
    <div>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.logoContainer}>
          <h1 style={styles.logo}>Auction Platform</h1>
        </div>
        <div style={styles.userContainer}>
          <nav style={styles.nav}>
            <a href="#home" style={styles.navLink}>
              Home
            </a>
            <a href="#categories" style={styles.navLink}>
              Contact Us
            </a>
            <a href="#sell" style={styles.navLink}>
              Sell
            </a>
            <a href="#login" style={styles.navLink}>
              Login
            </a>
          </nav>
          <div style={styles.userIconContainer} onClick={toggleDropdown} ref={dropdownRef}>
          <VscAccount style={styles.userIcon} />
          {isDropdownVisible && (
            <div style={styles.dropdown}>
              <span
                style={styles.dropdownItem}
                onMouseOver={(e) => (e.target.style.backgroundColor = '#f0f0f0')}
                onMouseOut={(e) => (e.target.style.backgroundColor = 'transparent')}
              >
                Settings
              </span>
              <span
                style={styles.dropdownItem}
                onMouseOver={(e) => (e.target.style.backgroundColor = '#f0f0f0')}
                onMouseOut={(e) => (e.target.style.backgroundColor = 'transparent')}
              >
                Logout
              </span>
            </div>
          )}
        </div>
        </div>
      </header>


    {/* Banner */}
    <section style={styles.banner}>
        <h2 style={styles.bannerText}>Welcome to Auction Platform - Your Destination for Exciting Bidding!</h2>
      </section>


      {/* Product Listing */}
      <section style={styles.productListing}>
        {/* Product Cards */}
        <div style={styles.productCard}>
          <img src="img/camera.jpg" alt="camera" style={styles.productImage} />
          <h3 style={styles.productTitle}>Luxury Watch</h3>
          <p style={styles.productDescription}>Elegant timepiece with advanced features.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        <div style={styles.productCard}>
          <img src="img/iphone.png" alt="iPhone" style={styles.productImage} />
          <h3 style={styles.productTitle}>Tech Gadgets Bundle</h3>
          <p style={styles.productDescription}>Stay up-to-date with the latest tech gadgets.</p>
          <button style={styles.bidButton}>Bid Now</button>
        </div>

        {/* Add more product cards as needed */}
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <p style={styles.footerText}>Auction Hub &copy; 2024</p>
        <p style={styles.footerText}><strong>Kaliraj M & Sridhar R</strong></p>
      </footer>
    </div>
  );
};

const styles = {
  header: {
    backgroundColor: '#1a237e',
    padding: '15px',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    position: 'sticky',
    top: 0,
    zIndex: 1000,
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logo: {
    margin: 0,
    fontSize: '24px',
    color: "#fff",
  },
  nav: {
    display: 'flex',
  },
  navLink: {
    color: '#fff',
    margin: '0 15px',
    textDecoration: 'none',
    fontSize: '16px',
  },

  userIconContainer: {
    position: 'relative',
    cursor: 'pointer',
  },

  userIcon: {
    width: '30px',
    height: '30px',
    borderRadius: '50%',
    margin: '0 15px',
  },

  dropdown: {
    position: 'absolute',
    top: '100%',
    right: 0,
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '5px',
    padding: '8px',
    zIndex: 1000,
    textAlign: 'right',
  },

  dropdownItem: {
    display: 'block',
    padding: '8px',
    cursor: 'pointer',
    textDecoration: 'none',
    color: '#333',
    transition: 'background-color 0.3s ease-in-out, color 0.3s ease-in-out',
  },

  dropdownItemHover: {
    backgroundColor: '#f0f0f0',
  },

  productListing: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    padding: '20px',
    marginTop: '70px', // Add top margin to avoid overlap with the header
  },

  userContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  
  productCard: {
    border: '1px solid #ccc',
    borderRadius: '8px',
    margin: '10px',
    padding: '15px',
    maxWidth: '300px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out',
    backgroundColor: '#fff',
    overflow: 'hidden',
  },
  productImage: {
    width: '100%',
    height: 'auto',
    borderRadius: '8px',
  },
  productTitle: {
    marginTop: '10px',
    marginBottom: '5px',
    fontSize: '18px',
  },
  productDescription: {
    color: '#555',
    fontSize: '14px',
  },
  bidButton: {
    backgroundColor: '#1a237e',
    color: '#fff',
    padding: '8px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginTop: '10px',
    fontSize: '14px',
    transition: 'background-color 0.3s ease-in-out',
  },
  bidButtonHover: {
    backgroundColor: '#3949ab',
  },
  footer: {
    backgroundColor: '#1a237e',
    color: '#fff',
    textAlign: 'center',
    padding: '10px',
    position: 'fixed',
    bottom: 0,
    width: '100%',
  },
  footerText: {
    margin: 0,
    color: "#fff",
    fontSize: '14px',
  },
  banner: {
    backgroundColor: '#4285f4',
    textAlign: 'center',
    color: '#fff',
    padding: '50px 0',
  },
  bannerText: {
    fontSize: '24px',
    margin: 0,
  },
};

export default HomePage;
