    dropdown: {
      position: 'absolute',
      top: '100%',
      right: 0,
      backgroundColor: '#fff',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      borderRadius: '5px',
      padding: '8px',
      zIndex: 1000,
      textAlign: 'right',
    },
  
    dropdownItem: {
      display: 'block',
      textDecoration: 'none',
      color: '#333',
        cursor: 'pointer',
        padding: '8px',
        transition: 'background-color 0.3s ease-in-out, color 0.3s ease-in-out',
    },
  
    dropdownItemHover: {
      backgroundColor: '#f0f0f0',
    },
    
      productImages: {
        flex: '1', // Occupy 50% of the available space for product images
      },
    
      productImage: {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
      },
    
      productTitle: {
        fontSize: '24px',
        marginBottom: '10px',
