      },
    
      input: {
        padding: '8px',
        marginBottom: '10px',
        fontSize: '16px',
        borderRadius: '5px',
        border: '1px solid #ccc',
      },
    
      placeBidButton: {
        backgroundColor: '#1a237e',
        color: '#fff',
        padding: '10px',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
        transition: 'background-color 0.3s ease-in-out',
      },
    
      allBidders: {
        padding: '20px',
        backgroundColor: '#fff', // Background color for the all bidders section
      },
    
      biddersTitle: {
        fontSize: '20px',
