        color: '#333',
      },
    
      biddersList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
      },
    
      bidderItem: {
        fontSize: '16px',
        color: '#555',
        marginBottom: '5px',
      },
  
    productListing: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      padding: '20px',
      marginTop: '70px', // Add top margin to avoid overlap with the header
    },
  
    userContainer: {
      display: 'flex',
      alignItems: 'center',
    },
    
    productCard: {
      border: '1px solid #ccc',
      borderRadius: '8px',
      margin: '10px',
      padding: '15px',
      maxWidth: '300px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
      transition: 'transform 0.3s ease-in-out',
      backgroundColor: '#fff',
      overflow: 'hidden',
    },

    bidButton: {
      backgroundColor: '#1a237e',
      color: '#fff',
      padding: '8px',
      border: 'none',
