import React, { useState } from 'react';

const SellerProductListing = () => {
  const [productData, setProductData] = useState({
    productName: '',
    productDescription: '',
    minBid: '',
    closingDate: '',
    productImage: null,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProductData({
      ...productData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setProductData({
      ...productData,
      productImage: e.target.files[0],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your logic to handle form submission (e.g., API call)
    console.log('Form submitted:', productData);
    // Reset the form after submission
    setProductData({
      productName: '',
      productDescription: '',
      minBid: '',
      closingDate: '',
      productImage: null,
    });
  };

  return (
    <div>
      <header style={styles.header}>
        <h1>Seller Product Listing</h1>
      </header>

      <form style={styles.form} onSubmit={handleSubmit}>
        <label htmlFor="productName">Product Name:</label>
        <input
          type="text"
          id="productName"
          name="productName"
          value={productData.productName}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="productDescription">Product Description:</label>
        <textarea
          id="productDescription"
          name="productDescription"
          rows="4"
          value={productData.productDescription}
          onChange={handleInputChange}
          required
        ></textarea>

        <label htmlFor="minBid">Minimum Bid:</label>
        <input
          type="number"
          id="minBid"
          name="minBid"
          value={productData.minBid}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="closingDate">Closing Date:</label>
        <input
          type="date"
          id="closingDate"
          name="closingDate"
          value={productData.closingDate}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="productImage">Product Image:</label>
        <input
          type="file"
          id="productImage"
          name="productImage"
          accept="image/*"
          onChange={handleFileChange}
          required
        />

        <button type="submit">List Product</button>
      </form>

      <footer style={styles.footer}>
        <p>&copy; 2024 Seller Platform</p>
      </footer>
    </div>
  );
};

const styles = {
  header: {
    backgroundColor: '#1a237e',
    color: '#fff',
    padding: '15px',
    textAlign: 'center',
  },
  form: {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
  },
  footer: {
    backgroundColor: '#1a237e',
    color: '#fff',
    textAlign: 'center',
    padding: '10px',
    position: 'fixed',
    bottom: 0,
    width: '100%',
  },
};

export default SellerProductListing;
