import React, { useState } from 'react';
import '../../css/sellerform.css';
import { useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';

function SellerForm() {
  // State variables to store form data
  const [auctionId, setAuctionId] = useState('');
  const [sellerId, setSellerId] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [startingPrice, setStartingPrice] = useState('');
  const [bidIncrement, setBidIncrement] = useState('');
  const [auctionEndTime, setAuctionEndTime] = useState('');
  const [status, setStatus] = useState('');

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to Spring Boot API
      const response = await fetch('http://localhost:8080/api/auction', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          auctionId,
          sellerId,
          itemDescription,
          startingPrice: parseFloat(startingPrice),
          bidIncrement: parseFloat(bidIncrement),
          auctionEndTime: new Date(auctionEndTime).toISOString(),
          status
        })
      });

      // Handle response
      if (response.ok) {
        console.log('Action created successfully');
        // Clear form after successful submission
        setAuctionId('');
        setSellerId('');
        setItemDescription('');
        setStartingPrice('');
        setBidIncrement('');
        setAuctionEndTime('');
        setStatus('');
      } else {
        console.error('Failed to create action');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Create Action</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="itemDescription">Item Description:</label>
          <input type="text" id="itemDescription" value={itemDescription} onChange={(e) => setItemDescription(e.target.value)} />
        </div>
        <div>
          <label htmlFor="startingPrice">Starting Price:</label>
          <input type="number" id="startingPrice" value={startingPrice} onChange={(e) => setStartingPrice(e.target.value)} />
        </div>
        <div>
          <label htmlFor="bidIncrement">Bid Increment:</label>
          <input type="number" id="bidIncrement" value={bidIncrement} onChange={(e) => setBidIncrement(e.target.value)} />
        </div>
        <div>
          <label htmlFor="auctionEndTime">Auction End Time:</label>
          <input type="datetime-local" id="auctionEndTime" value={auctionEndTime} onChange={(e) => setAuctionEndTime(e.target.value)} />
        </div>
        <div>
          <label htmlFor="status">Status:</label>
          <input type="text" id="status" value={status} onChange={(e) => setStatus(e.target.value)} />
        </div>
        <button type="submit">Create Action</button>
      </form>
    </div>
  );
}


export default SellerForm;
