// SignInPage.js

import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './SignInPage.css';

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();

  const handleSignIn = (e) => {
    e.preventDefault();

    // Basic password length validation
    if (password.length < 8) {
      setError('Password should be at least 8 characters long.');
      return;
    }

    // Implement your sign-in logic here
    // For demonstration purposes, let's assume the correct password is "demo123"
    if (password === 'demo123') {
      console.log('Signing in...');
      setError('');
      
      // Redirect to the BlankPage component
      history.push('/blank'); // Change '/blank' to your desired route
    } else {
      setError('Incorrect password. Please try again.');
    }
  };

  return (
    <div className="app-container">
      <h1 className="title">Your Company Name</h1>

      <div className="sign-in-container">
        <h2 className="sign-in-heading">Sign In</h2>
        <form className="sign-in-form" onSubmit={handleSignIn}>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <p className="error-message">{error}</p>}

          <div className="form-links">
            <Link to="/forgot-password">Forgot Password?</Link>
            <span className="separator">|</span>
            <Link to="/signup">Sign Up</Link>
          </div>

          <button type="submit" className="sign-in-button">
            Sign In
          </button>

          {/* Add the "Sign In to Blank Page" link */}
          <Link to="/blank">Sign In to Blank Page</Link>
        </form>
      </div>

      <div className="footer">
        <p>&copy; 2024 Your Company Name. All rights reserved.</p>
      </div>
    </div>
  );
};

export default SignInPage;
