import React, { useState, useEffect, useRef } from 'react';
import { VscAccount } from 'react-icons/vsc';
import { useNavigate } from 'react-router-dom';

const ProductDetailsPage = () => {
  const [isDropdownVisible, setDropdownVisibility] = useState(false);
  const [bidAmount, setBidAmount] = useState('');
  const [bidders, setBidders] = useState([
    { name: 'Bidder 1', amount: 550 },
    { name: 'Bidder 2', amount: 600 },
    { name: 'Bidder 3', amount: 650 },
  ]);

  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  const toggleDropdown = () => {
    setDropdownVisibility(!isDropdownVisible);
  };

  const closeDropdown = () => {
    setDropdownVisibility(false);
  };

  const handleLogout = () => {
    navigate('/');
  };

  const handleBid = () => {
    // Update the list of bidders with the new bid
    const newBidder = { name: 'You', amount: parseInt(bidAmount) };
    setBidders([newBidder, ...bidders]);
    // You can also send the bid to the server or perform other actions as needed
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        closeDropdown();
      }
    };

    document.addEventListener('click', handleClickOutside);

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isDropdownVisible]);

  const calculateTimeRemaining = () => {
    const closingDate = new Date('2024-01-31T00:00:00'); // Replace with your actual closing date
    const currentDate = new Date();

    const timeDifference = closingDate - currentDate;
    const days = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
    const hours = Math.floor((timeDifference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((timeDifference % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((timeDifference % (1000 * 60)) / 1000);

    return { days, hours, minutes, seconds };
  };

  const renderTimeRemaining = () => {
    const { days, hours, minutes, seconds } = calculateTimeRemaining();
    return `${days} days ${hours} hours ${minutes} minutes ${seconds} seconds`;
  };
  const [timeRemaining, setTimeRemaining] = useState(calculateTimeRemaining());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTimeRemaining(calculateTimeRemaining());
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.logoContainer}>
          <h1 style={styles.logo}>Auction Platform</h1>
        </div>
        <div style={styles.userContainer}>
          <nav style={styles.nav}>
            <a href="/homepage" style={styles.navLink}>
              Home
            </a>
            <a href="#categories" style={styles.navLink}>
              Contact Us
            </a>
            <a href="#sell" style={styles.navLink}>
              Sell
            </a>
          </nav>
          <div style={styles.userIconContainer} onClick={toggleDropdown} ref={dropdownRef}>
            <VscAccount style={styles.userIcon} />
            {isDropdownVisible && (
              <div style={styles.dropdown}>
                <span
                  style={styles.dropdownItem}
                  onMouseOver={(e) => (e.target.style.backgroundColor = '#f0f0f0')}
                  onMouseOut={(e) => (e.target.style.backgroundColor = 'transparent')}
                >
                  Settings
                </span>
                <span
                  style={styles.dropdownItem}
                  onMouseOver={(e) => (e.target.style.backgroundColor = '#f0f0f0')}
                  onMouseOut={(e) => (e.target.style.backgroundColor = 'transparent')}
                  onClick={handleLogout}
                >
                  Logout
                </span>
              </div>
            )}
          </div>
        </div>
      </header>

            {/* Product Details */}
      <section style={styles.productDetails}>
        <div style={styles.productImages}>
          {/* Include product images here */}
          <img src="img/camera.jpg" alt="camera" style={styles.productImage} />
          {/* Add more images as needed */}
        </div>
        <div style={styles.productInfo}>
          <h2 style={styles.productTitle}>Luxury Watch</h2>
          <p style={styles.productDescription}>Elegant timepiece with advanced features.</p>
          <p style={styles.minBid}>Minimum Bid: $500</p>
          <p style={styles.closingDate}>Closing Date: January 31, 2024</p>
          <p style={styles.remainingTime}>Time Remaining: {renderTimeRemaining()}</p>
          {/* Add bid option and form here */}
          <form style={styles.bidForm} onSubmit={handleBid}>
            <label htmlFor="bidAmount">Your Bid:</label>
            <input
              type="text"
              id="bidAmount"
              name="bidAmount"
              value={bidAmount}
              onChange={(e) => setBidAmount(e.target.value)}
            />
            <button style={styles.placeBidButton} type="submit">
              Place Bid
            </button>
          </form>
        </div>
      </section>

      {/* All Bidders */}
      <section style={styles.allBidders}>
        <h3 style={styles.biddersTitle}>All Bidders</h3>
        {/* Display a list of all bidders and their bid prices */}
        <ul style={styles.biddersList}>
          {bidders.map((bidder, index) => (
            <li key={index}>{`${bidder.name} - $${bidder.amount}`}</li>
          ))}
        </ul>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <p style={styles.footerText}>Auction Hub &copy; 2024</p>
        <p style={styles.footerText}>
          <strong>Kaliraj M & Sridhar R</strong>
        </p>
      </footer>
    </div>
  );
};


const styles = {

  header: {
    backgroundColor: '#1a237e',
    padding: '15px',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    position: 'fixed', 
    top: 0,
    width: '100%', 
    zIndex: 1000,
  },

  productDetails: {
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'flex-start',
    padding: '20px',
    backgroundColor: '#f4f4f4',
    marginTop: '70px',
  },

  productInfo: {
    flex: '1',
    marginLeft: '20px',
    textAlign: 'left', 
  },

    logoContainer: {
      display: 'flex',
      alignItems: 'center',
    },
    logo: {
      margin: 0,
      fontSize: '24px',
      color: "#fff",
    },
    nav: {
      display: 'flex',
    },
    navLink: {
      color: '#fff',
      margin: '0 15px',
      textDecoration: 'none',
      fontSize: '16px',
    },
  
    userIconContainer: {
      position: 'relative',
      cursor: 'pointer',
    },
    userIcon: {
      width: '30px',
      height: '30px',
      borderRadius: '50%',
      margin: '0 15px',
    },
  
    dropdown: {
      position: 'absolute',
      top: 'calc(100% + 10px)', // Adjust the value as needed
      right: 0,
      backgroundColor: '#fff',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      borderRadius: '5px',
      padding: '8px',
      zIndex: 1000,
      textAlign: 'right',
    },
  
    dropdownItem: {
      display: 'block',
      textDecoration: 'none',
      color: '#333',
        cursor: 'pointer',
