import React, { useState } from 'react';
import '../../css/sellerform.css';
import { Link, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';

function SellerForm() {
  const [itemName, setItemName] = useState('');
  const [startingPrice, setStartingPrice] = useState('');
  const [auctionStartTime, setAuctionStartTime] = useState('');
  const [auctionEndTime, setAuctionEndTime] = useState('');
  const [bidIncrement, setBidIncrement] = useState('');
  const [productImage, setProductImage] = useState(null); // Use null instead of ''
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('itemName', itemName);
    formData.append('startingPrice', startingPrice);
    formData.append('auctionStartTime', auctionStartTime);
    formData.append('auctionEndTime', auctionEndTime);
    formData.append('bidIncrement', bidIncrement);
    formData.append('productImage', productImage);
    formData.append('description', description);

    try {
      const response = await fetch('http://localhost:8080/api/auctions/create', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        // Auction created successfully
        navigate('/success'); // Redirect to a success page or handle as needed
      } else {
        // Handle error response
        console.error('Failed to create auction');
      }
    } catch (error) {
      // Handle network error
      console.error('Network error:', error);
    }
  };

  return (
    <div>
      <Helmet>
        <title>Create Auction</title>
      </Helmet>
      <h1>Create Auction</h1>
      <form onSubmit={handleFormSubmit}>
        <label>Item Name:</label>
        <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} required />

        <label>Starting Price:</label>
        <input type="number" value={startingPrice} onChange={(e) => setStartingPrice(e.target.value)} required />

        <label>Auction Start Time:</label>
        <input type="datetime-local" value={auctionStartTime} onChange={(e) => setAuctionStartTime(e.target.value)} required />

        <label>Auction End Time:</label>
        <input type="datetime-local" value={auctionEndTime} onChange={(e) => setAuctionEndTime(e.target.value)} required />

        <label>Bid Increment:</label>
        <input type="number" value={bidIncrement} onChange={(e) => setBidIncrement(e.target.value)} required />

        <label>Product Image:</label>
        <input type="file" onChange={(e) => setProductImage(e.target.files[0])} required />

        <label>Description:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} />

        <button type="submit">Create Auction</button>
      </form>
    </div>
  );
}

export default SellerForm;










